import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useTranslation } from 'react-i18next';
import './i18n';
import axios from 'axios';

function App() {
  const { t, i18n } = useTranslation();
  const [lang, setLang] = useState('en');
  const [principal, setPrincipal] = useState('');
  const [rate, setRate] = useState('');
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [type, setType] = useState('simple');
  const [interest, setInterest] = useState(null);

  const changeLang = (e) => {
    const newLang = e.target.value;
    i18n.changeLanguage(newLang);
    setLang(newLang);
  };

  const calculate = async () => {
    const resp = await axios.post('http://localhost:5000/api/calculate', {
      principal: parseFloat(principal),
      rate: parseFloat(rate),
      startDate,
      endDate,
      type
    });
    setInterest(resp.data.interest);
  };

  return (
    <div style={{ padding: 0 }}>
      <select value={lang} onChange={changeLang}>
        <option value="en">English</option>
        <option value="hi">Hindi</option>
        <option value="te">Telugu</option>
      </select>
      <div><label>{t('principal')}:</label><input value={principal} onChange={e => setPrincipal(e.target.value)} /></div>
      <div><label>{t('rate')}:</label><input value={rate} onChange={e => setRate(e.target.value)} /></div>
      <div><label>{t('start')}:</label><DatePicker selected={startDate} onChange={setStartDate} /></div>
      <div><label>{t('end')}:</label><DatePicker selected={endDate} onChange={setEndDate} /></div>
      <div>
        <label>{t('type')}:</label>
        <select value={type} onChange={e => setType(e.target.value)}>
          <option value="simple">{t('simple')}</option>
          <option value="compound">{t('compound')}</option>
        </select>
      </div>
      <button onClick={calculate}>{t('calculate')}</button>
      {interest !== null && <h3>{t('result')}: {interest.toFixed(2)}</h3>}
    </div>
  );
}
export default App;
