import React, { useState } from 'react';
import './App.css';

function App() {
  const [principal, setPrincipal] = useState('');
  const [rate, setRate] = useState('');
  const [ratePeriod, setRatePeriod] = useState('year');
  const [frequency, setFrequency] = useState('yearly');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [language, setLanguage] = useState('en');

  const labels = {
    en: {
      principal: "Principal",
      rate: "Rate (%)",
      per: "per",
      startDate: "Start Date",
      endDate: "End Date",
      frequency: "Frequency",
      calculate: "Calculate",
      clear: "Clear",
      interestAmount: "Interest Amount",
      finalAmount: "Final Amount",
      timePeriod: "Time Period",
      title: "Interest Calculator",
      error: "Please fill in all fields correctly."
    },
    hi: {
      principal: "‡§Æ‡•Ç‡§≤‡§ß‡§®",
      rate: "‡§¨‡•ç‡§Ø‡§æ‡§ú ‡§¶‡§∞ (%)",
      per: "‡§™‡•ç‡§∞‡§§‡§ø",
      startDate: "‡§™‡•ç‡§∞‡§æ‡§∞‡§Ç‡§≠ ‡§§‡§ø‡§•‡§ø",
      endDate: "‡§∏‡§Æ‡§æ‡§™‡•ç‡§§‡§ø ‡§§‡§ø‡§•‡§ø",
      frequency: "‡§Ü‡§µ‡•É‡§§‡•ç‡§§‡§ø",
      calculate: "‡§ó‡§£‡§®‡§æ ‡§ï‡§∞‡•á‡§Ç",
      clear: "‡§∞‡•Ä‡§∏‡•á‡§ü ‡§ï‡§∞‡•á‡§Ç",
      interestAmount: "‡§¨‡•ç‡§Ø‡§æ‡§ú ‡§∞‡§æ‡§∂‡§ø",
      finalAmount: "‡§Ö‡§Ç‡§§‡§ø‡§Æ ‡§∞‡§æ‡§∂‡§ø",
      timePeriod: "‡§∏‡§Æ‡§Ø ‡§Ö‡§µ‡§ß‡§ø",
      title: "‡§¨‡•ç‡§Ø‡§æ‡§ú ‡§ï‡•à‡§≤‡§ï‡•Å‡§≤‡•á‡§ü‡§∞",
      error: "‡§ï‡•É‡§™‡§Ø‡§æ ‡§∏‡§≠‡•Ä ‡§´‡§º‡•Ä‡§≤‡•ç‡§° ‡§∏‡§π‡•Ä ‡§∏‡•á ‡§≠‡§∞‡•á‡§Ç‡•§"
    },
    te: {
      principal: "‡∞™‡±ç‡∞∞‡∞ø‡∞®‡±ç‡∞∏‡∞ø‡∞™‡∞≤‡±ç",
      rate: "‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞∞‡±á‡∞ü‡±Å (%)",
      per: "‡∞™‡±ç‡∞∞‡∞§‡∞ø",
      startDate: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠ ‡∞§‡±á‡∞¶‡±Ä",
      endDate: "‡∞Æ‡±Å‡∞ó‡∞ø‡∞Ç‡∞™‡±Å ‡∞§‡±á‡∞¶‡±Ä",
      frequency: "‡∞§‡±Ä‡∞µ‡±ç‡∞∞‡∞§",
      calculate: "‡∞≤‡±Ü‡∞ï‡±ç‡∞ï‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø",
      clear: "‡∞∞‡±Ä‡∞∏‡±Ü‡∞ü‡±ç",
      interestAmount: "‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞Æ‡±ä‡∞§‡±ç‡∞§‡∞Ç",
      finalAmount: "‡∞ö‡∞ø‡∞µ‡∞∞‡∞ø ‡∞Æ‡±ä‡∞§‡±ç‡∞§‡∞Ç",
      timePeriod: "‡∞∏‡∞Æ‡∞Ø‡∞Ç",
      title: "‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞ó‡∞£‡∞ï‡∞Æ‡±Å",
      error: "‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞Ö‡∞®‡±ç‡∞®‡∞ø ‡∞´‡±Ä‡∞≤‡±ç‡∞°‡±ç‡∞∏‡±ç ‡∞∏‡∞∞‡∞ø‡∞ó‡±ç‡∞ó‡∞æ ‡∞≠‡∞∞‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø."
    }
  };

  const toggleTheme = () => setDarkMode(!darkMode);

  const clearAll = () => {
    setPrincipal('');
    setRate('');
    setRatePeriod('year');
    setFrequency('yearly');
    setStartDate('');
    setEndDate('');
    setResult(null);
    setError('');
  };

  const calculateTime = () => {
    if (!startDate || !endDate) return 0;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffMs = end - start;
    if (diffMs <= 0) return 0;

    if (ratePeriod === 'day') {
      return diffMs / (1000 * 60 * 60 * 24);
    } else if (ratePeriod === 'month') {
      return (end.getFullYear() - start.getFullYear()) * 12 + (end.getMonth() - start.getMonth()) + (end.getDate() >= start.getDate() ? 0 : -1);
    } else {
      return diffMs / (1000 * 60 * 60 * 24 * 365.25);
    }
  };

  const calculateInterest = () => {
    setError('');
    if (!principal || !rate || !startDate || !endDate) {
      setError(labels[language].error);
      setResult(null);
      return;
    }

    let P = parseFloat(principal);
    let R = parseFloat(rate);
    let T = calculateTime();

    if (isNaN(P) || isNaN(R) || T <= 0) {
      setError(labels[language].error);
      setResult(null);
      return;
    }

    let A, interest;

    if (frequency === 'simple') {
      interest = (P * R * T) / 100;
      A = P + interest;
    } else if (frequency === 'continuous') {
      A = P * Math.exp((R / 100) * T);
      interest = A - P;
    } else {
      A = P * Math.pow(1 + R / 100, T);
      interest = A - P;
    }

    setResult({
      interest: interest.toFixed(2),
      amount: A.toFixed(2),
      time: T.toFixed(2),
    });
  };

  return (
    <div className={darkMode ? 'app dark' : 'app'}>
      <div className="header">
        <h1>{labels[language].title}</h1>
        <div className="header-controls">
          <select
            className="language-select"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="hi">‡§π‡§ø‡§®‡•ç‡§¶‡•Ä</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
          </select>
          <button className="theme-toggle" onClick={toggleTheme}>
            {darkMode ? '‚òÄÔ∏è Light' : 'üåô Dark'}
          </button>
        </div>
      </div>

      <div className="form-horizontal">
        <div className="form-group">
          <label>{labels[language].principal}:</label>
          <input
            type="number"
            value={principal}
            onChange={(e) => setPrincipal(e.target.value)}
          />
        </div>

        <div className="form-group rate-group">
          <label>{labels[language].rate}:</label>
          <div className="rate-input">
            <input
              type="number"
              value={rate}
              onChange={(e) => setRate(e.target.value)}
            />
            <select value={ratePeriod} onChange={(e) => setRatePeriod(e.target.value)}>
              <option value="year">{labels[language].per} year</option>
              <option value="month">{labels[language].per} month</option>
              <option value="day">{labels[language].per} day</option>
            </select>
          </div>
        </div>

        <div className="form-group">
          <label>{labels[language].startDate}:</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>{labels[language].endDate}:</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>{labels[language].frequency}:</label>
          <select value={frequency} onChange={(e) => setFrequency(e.target.value)}>
            <option value="simple">Simple Interest</option>
            <option value="daily">Compound Daily</option>
            <option value="monthly">Compound Monthly</option>
            <option value="quarterly">Compound Quarterly</option>
            <option value="yearly">Compound Yearly</option>
            <option value="continuous">Continuous Compounding</option>
          </select>
        </div>

        <button onClick={calculateInterest}>{labels[language].calculate}</button>
        <button onClick={clearAll}>{labels[language].clear}</button>
      </div>

      {error && <div className="error">{error}</div>}

      {result && (
        <div className="result">
          <p>{labels[language].timePeriod}: {result.time}</p>
          <p>{labels[language].interestAmount}: {result.interest}</p>
          <p>{labels[language].finalAmount}: {result.amount}</p>
        </div>
      )}
    </div>
  );
}

export default App;
