import React, { useState } from 'react';
import './App.css';

function App() {
  const [principal, setPrincipal] = useState('');
  const [rate, setRate] = useState('');
  const [frequency, setFrequency] = useState('yearly');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  const toggleTheme = () => setDarkMode(!darkMode);

  const clearAll = () => {
    setPrincipal('');
    setRate('');
    setFrequency('yearly');
    setStartDate('');
    setEndDate('');
    setResult(null);
    setError('');
  };

  const calculateTime = () => {
    if (!startDate || !endDate) return 0;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffYears = (end - start) / (1000 * 60 * 60 * 24 * 365.25);
    return Math.max(diffYears, 0);
  };

  const calculateInterest = () => {
    setError('');
    if (!principal || !rate || !startDate || !endDate) {
      setError('Please fill in all fields.');
      setResult(null);
      return;
    }

    const P = parseFloat(principal);
    const R = parseFloat(rate) / 100;
    const T = calculateTime();

    if (isNaN(P) || isNaN(R) || T <= 0) {
      setError('Invalid input values.');
      setResult(null);
      return;
    }

    let A, interest;

    if (frequency === 'simple') {
      interest = P * R * T;
      A = P + interest;
    } else if (frequency === 'continuous') {
      A = P * Math.exp(R * T);
      interest = A - P;
    } else {
      let n;
      switch (frequency) {
        case 'daily':
          n = 365;
          break;
        case 'monthly':
          n = 12;
          break;
        case 'quarterly':
          n = 4;
          break;
        case 'yearly':
          n = 1;
          break;
        default:
          n = 1;
      }
      A = P * Math.pow(1 + R / n, n * T);
      interest = A - P;
    }

    setResult({
      interest: interest.toFixed(2),
      amount: A.toFixed(2),
      time: T.toFixed(2),
    });
  };

  return (
    <div className={darkMode ? 'app dark' : 'app'}>
      <h1>Interest Calculator</h1>
      <button onClick={toggleTheme}>
        {darkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
      </button>

      <div className="form">
        <label>Principal:</label>
        <input
          type="number"
          value={principal}
          onChange={(e) => setPrincipal(e.target.value)}
        />

        <label>Rate (% per year):</label>
        <input
          type="number"
          value={rate}
          onChange={(e) => setRate(e.target.value)}
        />

        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />

        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />

        <label>Frequency:</label>
        <select value={frequency} onChange={(e) => setFrequency(e.target.value)}>
          <option value="simple">Simple Interest</option>
          <option value="daily">Compound Daily</option>
          <option value="monthly">Compound Monthly</option>
          <option value="quarterly">Compound Quarterly</option>
          <option value="yearly">Compound Yearly</option>
          <option value="continuous">Continuous Compounding</option>
        </select>

        <button onClick={calculateInterest}>Calculate</button>
        <button onClick={clearAll}>Clear</button>
      </div>

      {error && <div className="error">{error}</div>}

      {result && (
        <div className="result">
          <p>Time Period: {result.time} years</p>
          <p>Interest Amount: {result.interest}</p>
          <p>Final Amount: {result.amount}</p>
        </div>
      )}
    </div>
  );
}

export default App;
