      interestAmount: '‡§¨‡•ç‡§Ø‡§æ‡§ú ‡§∞‡§æ‡§∂‡§ø',
      finalAmount: '‡§Ö‡§Ç‡§§‡§ø‡§Æ ‡§∞‡§æ‡§∂‡§ø',
      loanAmount: '‡§ã‡§£ ‡§∞‡§æ‡§∂‡§ø',
      tenureMonths: '‡§Ö‡§µ‡§ß‡§ø (‡§Æ‡§π‡•Ä‡§®‡•á)',
      emiResult: '‡§Æ‡§æ‡§∏‡§ø‡§ï ‡§à‡§è‡§Æ‡§Ü‡§à',
      light: '‡§≤‡§æ‡§á‡§ü',
      dark: '‡§°‡§æ‡§∞‡•ç‡§ï',
      mode: '‡§Æ‡•ã‡§°',
    },
    te: {
      interestCalculator: '‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞ó‡∞£‡∞ï‡∞Ç',
      emiCalculator: '‡∞à‡∞é‡∞Ç‡∞ê ‡∞ó‡∞£‡∞ï‡∞Ç',
      selectCalculator: '‡∞ï‡±ç‡∞Ø‡∞æ‡∞≤‡±ç‡∞ï‡±Å‡∞≤‡±á‡∞ü‡∞∞‡±ç ‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø',
      principal: '‡∞™‡±ç‡∞∞‡∞ø‡∞®‡±ç‡∞∏‡∞ø‡∞™‡∞≤‡±ç',
      rate: '‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞∞‡±á‡∞ü‡±Å (%)',
      ratePeriod: '‡∞™‡±ç‡∞∞‡∞§‡∞ø ‡∞∏‡∞Ç‡∞µ‡∞§‡±ç‡∞∏‡∞∞‡∞Ç / ‡∞®‡±Ü‡∞≤ / ‡∞∞‡±ã‡∞ú‡±Å',
      startDate: '‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠ ‡∞§‡±á‡∞¶‡±Ä',
      endDate: '‡∞Æ‡±Å‡∞ó‡∞ø‡∞Ç‡∞™‡±Å ‡∞§‡±á‡∞¶‡±Ä',
      frequency: '‡∞∞‡∞ï‡∞Ç',
      calculate: '‡∞≤‡±Ü‡∞ï‡±ç‡∞ï‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø',
      clear: '‡∞∞‡±Ä‡∞∏‡±Ü‡∞ü‡±ç',
      timePeriod: '‡∞∏‡∞Æ‡∞Ø‡∞Ç (‡∞∏‡∞Ç‡∞µ‡∞§‡±ç‡∞∏‡∞∞‡∞æ‡∞≤‡±Å)',
      interestAmount: '‡∞µ‡∞°‡±ç‡∞°‡±Ä ‡∞Æ‡±ä‡∞§‡±ç‡∞§‡∞Ç',
      finalAmount: '‡∞ö‡∞ø‡∞µ‡∞∞‡∞ø ‡∞Æ‡±ä‡∞§‡±ç‡∞§‡∞Ç',
      loanAmount: '‡§ã‡∞£ ‡∞Æ‡±ä‡∞§‡±ç‡∞§‡∞Ç',
      tenureMonths: '‡∞ï‡∞æ‡∞≤‡∞Ç (‡∞®‡±Ü‡∞≤‡∞≤‡±Å)',
      emiResult: '‡∞Æ‡∞æ‡∞∏‡∞™‡±Å ‡∞à‡∞é‡∞Ç‡∞ê',
      light: '‡∞≤‡±à‡∞ü‡±ç',
      dark: '‡∞°‡∞æ‡∞∞‡±ç‡∞ï‡±ç',
      mode: '‡∞Æ‡±ã‡∞°‡±ç',
    },
  };

  const handleThemeToggle = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  // Interest Calculator Logic
  const calculateInterest = () => {
    if (!principal || !rate || !startDate || !endDate) {
      alert('Please fill all fields!');
      return;
    }

    const p = parseFloat(principal);
    let r = parseFloat(rate);
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diff = (end - start) / (1000 * 60 * 60 * 24) / 365; // years

    let T = diff;
    if (T <= 0) {
      alert('End date must be after start date!');
      return;
    }

    // Adjust rate per selected period
    if (ratePeriod === 'per month') {
      r = r * 12;
    } else if (ratePeriod === 'per day') {
      r = r * 365;
    }

    let interestAmount = 0;
    let finalAmount = 0;

    if (interestType === 'Simple Interest') {
      interestAmount = (p * r * T) / 100;
      finalAmount = p + interestAmount;
    } else if (interestType === 'Compound Interest') {
      finalAmount = p * Math.pow((1 + r / 100), T);
      interestAmount = finalAmount - p;
    }

    setResult({ timePeriod: T.toFixed(2), interestAmount: interestAmount.toFixed(2), finalAmount: finalAmount.toFixed(2) });
  };

  const clearInterest = () => {
    setPrincipal('');
    setRate('');
    setRatePeriod('per year');
    setStartDate('');
    setEndDate('');
    setInterestType('Simple Interest');
    setResult(null);
  };

  // EMI Calculator Logic
  const calculateEMI = () => {
    if (!loanAmount || !emiRate || !tenureMonths) {
      alert('Please fill all fields!');
      return;
    }

    const P = parseFloat(loanAmount);
    const R = parseFloat(emiRate) / (12 * 100);
    const N = parseInt(tenureMonths);

    const emi = (P * R * Math.pow(1 + R, N)) / (Math.pow(1 + R, N) - 1);

    setEmiResult(emi.toFixed(2));
  };

  const clearEMI = () => {
    setLoanAmount('');
    setEmiRate('');
    setTenureMonths('');
    setEmiResult(null);
  };

  return (
    <div className={`app ${theme}`}>
      <div className="header">
        <h1>{labels[language][calculatorType === 'interest' ? 'interestCalculator' : 'emiCalculator']}</h1>
        <div className="header-controls">
          <div className="language-selector">
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="en">English</option>
              <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
              <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            </select>
          </div>
          <button onClick={handleThemeToggle}>
            {theme === 'light' ? 'üåû ' + labels[language].light : 'üåô ' + labels[language].dark}
          </button>
        </div>
      </div>

      <div className="calculator-selector">
        <label>{labels[language].selectCalculator}:</label>
        <select value={calculatorType} onChange={(e) => setCalculatorType(e.target.value)}>
          <option value="interest">{labels[language].interestCalculator}</option>
          <option value="emi">{labels[language].emiCalculator}</option>
        </select>
      </div>

      {calculatorType === 'interest' && (
        <>
          <div className="form-row">
            <label>
              {labels[language].principal}:
              <input type="number" value={principal} onChange={(e) => setPrincipal(e.target.value)} />
            </label>
            <label>
              {labels[language].rate}:
              <input type="number" value={rate} onChange={(e) => setRate(e.target.value)} />
            </label>
            <label>
              <select value={ratePeriod} onChange={(e) => setRatePeriod(e.target.value)}>
                <option value="per year">per year</option>
                <option value="per month">per month</option>
                <option value="per day">per day</option>
              </select>
            </label>
            <label>
              {labels[language].startDate}:
              <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
            </label>
            <label>
              {labels[language].endDate}:
              <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
            </label>
            <label>
              {labels[language].frequency}:
              <select value={interestType} onChange={(e) => setInterestType(e.target.value)}>
                <option value="Simple Interest">Simple Interest</option>
                <option value="Compound Interest">Compound Interest</option>
              </select>
            </label>
            <button onClick={calculateInterest}>{labels[language].calculate}</button>
            <button onClick={clearInterest}>{labels[language].clear}</button>
          </div>
          {result && (
            <div className="result">
              <div>{labels[language].timePeriod}: {result.timePeriod}</div>
              <div>{labels[language].interestAmount}: {result.interestAmount}</div>
              <div>{labels[language].finalAmount}: {result.finalAmount}</div>
            </div>
          )}
        </>
      )}

      {calculatorType === 'emi' && (
        <>
          <div className="form-row">
            <label>
              {labels[language].loanAmount}:
              <input type="number" value={loanAmount} onChange={(e) => setLoanAmount(e.target.value)} />
            </label>
            <label>
              {labels[language].rate}:
              <input type="number" value={emiRate} onChange={(e) => setEmiRate(e.target.value)} />
            </label>
            <label>
              {labels[language].tenureMonths}:
              <input type="number" value={tenureMonths} onChange={(e) => setTenureMonths(e.target.value)} />
            </label>
            <button onClick={calculateEMI}>{labels[language].calculate}</button>
            <button onClick={clearEMI}>{labels[language].clear}</button>
          </div>
          {emiResult && (
            <div className="result">
              <div>{labels[language].emiResult}: {emiResult}</div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

