import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('interest');
  const [language, setLanguage] = useState('en');
  const [darkMode, setDarkMode] = useState(false);

  // Interest Calculator States
  const [principal, setPrincipal] = useState('');
  const [rate, setRate] = useState('');
  const [ratePeriod, setRatePeriod] = useState('year');
  const [frequency, setFrequency] = useState('simple');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [interestResult, setInterestResult] = useState(null);
  const [interestError, setInterestError] = useState('');

  // EMI Calculator States
  const [loanAmount, setLoanAmount] = useState('');
  const [loanRate, setLoanRate] = useState('');
  const [tenure, setTenure] = useState('');
  const [tenureType, setTenureType] = useState('months');
  const [emiResult, setEmiResult] = useState(null);
  const [emiError, setEmiError] = useState('');

  const labels = {
    en: {
      interestCalculator: "Interest Calculator",
      emiCalculator: "EMI Calculator",
      principal: "Principal",
      rate: "Rate (%)",
      per: "per",
      startDate: "Start Date",
      endDate: "End Date",
      frequency: "Frequency",
      calculate: "Calculate",
      clear: "Clear",
      interestAmount: "Interest Amount",
      finalAmount: "Final Amount",
      timePeriod: "Time Period",
      loanAmount: "Loan Amount",
      tenure: "Tenure",
      tenureType: "Tenure Type",
      emi: "EMI",
      totalInterest: "Total Interest",
      totalPayment: "Total Payment",
      error: "Please fill in all fields correctly."
    },
    // Add Hindi / Telugu here...
  };

  const t = labels[language];

  const calculateTimeInYears = () => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffMs = end - start;
    return diffMs > 0 ? diffMs / (1000 * 60 * 60 * 24 * 365.25) : 0;
  };

  const getAnnualRate = () => {
    if (ratePeriod === 'year') return parseFloat(rate);
    if (ratePeriod === 'month') return parseFloat(rate) * 12;
    if (ratePeriod === 'day') return parseFloat(rate) * 365;
    return parseFloat(rate);
  };

  const calculateInterest = () => {
    setInterestError('');
    if (!principal || !rate || !startDate || !endDate) {
      setInterestError(t.error);
      return;
    }

    const P = parseFloat(principal);
    const R = getAnnualRate();
    const T = calculateTimeInYears();

    if (isNaN(P) || isNaN(R) || T <= 0) {
      setInterestError(t.error);
      return;
    }

    let A, interest;

    if (frequency === 'simple') {
      interest = (P * R * T) / 100;
      A = P + interest;
    } else if (frequency === 'continuous') {
      A = P * Math.exp((R / 100) * T);
      interest = A - P;
    } else {
      A = P * Math.pow(1 + R / 100, T);
      interest = A - P;
    }

    setInterestResult({
      interest: interest.toFixed(2),
      amount: A.toFixed(2),
      time: T.toFixed(2),
    });
  };

  const clearInterest = () => {
    setPrincipal('');
    setRate('');
    setRatePeriod('year');
    setFrequency('simple');
    setStartDate('');
    setEndDate('');
    setInterestResult(null);
    setInterestError('');
  };

  const calculateEMI = () => {
    setEmiError('');
    if (!loanAmount || !loanRate || !tenure) {
      setEmiError(t.error);
      return;
    }

    const P = parseFloat(loanAmount);
    const annualRate = parseFloat(loanRate);
    const tenureMonths = tenureType === 'years' ? parseInt(tenure) * 12 : parseInt(tenure);
    const R = annualRate / 12 / 100;

    if (isNaN(P) || isNaN(R) || tenureMonths <= 0) {
      setEmiError(t.error);
      return;
    }

    const emi = P * R * Math.pow(1 + R, tenureMonths) / (Math.pow(1 + R, tenureMonths) - 1);
    const totalPayment = emi * tenureMonths;
    const totalInterest = totalPayment - P;

    setEmiResult({
      emi: emi.toFixed(2),
      totalPayment: totalPayment.toFixed(2),
      totalInterest: totalInterest.toFixed(2)
    });
  };

  const clearEMI = () => {
    setLoanAmount('');
    setLoanRate('');
    setTenure('');
    setTenureType('months');
    setEmiResult(null);
    setEmiError('');
  };

  return (
    <div className={`app ${darkMode ? 'dark' : ''}`}>
      <div className="header">
        <h1>{activeTab === 'interest' ? t.interestCalculator : t.emiCalculator}</h1>
        <div className="header-controls">
          <select value={language} onChange={(e) => setLanguage(e.target.value)}>
            <option value="en">English</option>
            {/* Add more languages */}
          </select>
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '☀️ Light' : '🌙 Dark'}
          </button>
        </div>
      </div>

      <div className="tabs">
        <button onClick={() => setActiveTab('interest')}>{t.interestCalculator}</button>
        <button onClick={() => setActiveTab('emi')}>{t.emiCalculator}</button>
      </div>

      <div className="content-row">
        {activeTab === 'interest' && (
          <div className="module">
            <div className="form">
              <label>{t.principal}:</label>
              <input type="number" value={principal} onChange={(e) => setPrincipal(e.target.value)} />

              <label>{t.rate}:</label>
              <input type="number" value={rate} onChange={(e) => setRate(e.target.value)} />
              <select value={ratePeriod} onChange={(e) => setRatePeriod(e.target.value)}>
                <option value="year">{t.per} year</option>
                <option value="month">{t.per} month</option>
                <option value="day">{t.per} day</option>
              </select>

              <label>{t.startDate}:</label>
              <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
              <label>{t.endDate}:</label>
              <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />

              <label>{t.frequency}:</label>
              <select value={frequency} onChange={(e) => setFrequency(e.target.value)}>
                <option value="simple">Simple Interest</option>
                <option value="continuous">Continuous</option>
                <option value="yearly">Compound Yearly</option>
              </select>

              <button onClick={calculateInterest}>{t.calculate}</button>
              <button onClick={clearInterest}>{t.clear}</button>
            </div>

            {interestError && <p className="error">{interestError}</p>}
            {interestResult && (
              <div className="result">
                <p>{t.timePeriod}: {interestResult.time}</p>
                <p>{t.interestAmount}: {interestResult.interest}</p>
                <p>{t.finalAmount}: {interestResult.amount}</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'emi' && (
          <div className="module">
            <div className="form">
              <label>{t.loanAmount}:</label>
              <input type="number" value={loanAmount} onChange={(e) => setLoanAmount(e.target.value)} />

              <label>{t.rate}:</label>
              <input type="number" value={loanRate} onChange={(e) => setLoanRate(e.target.value)} />

              <label>{t.tenure}:</label>
              <input type="number" value={tenure} onChange={(e) => setTenure(e.target.value)} />
              <select value={tenureType} onChange={(e) => setTenureType(e.target.value)}>
                <option value="months">Months</option>
                <option value="years">Years</option>
              </select>

              <button onClick={calculateEMI}>{t.calculate}</button>
              <button onClick={clearEMI}>{t.clear}</button>
            </div>

            {emiError && <p className="error">{emiError}</p>}
            {emiResult && (
              <div className="result">
                <p>{t.emi}: {emiResult.emi}</p>
                <p>{t.totalInterest}: {emiResult.totalInterest}</p>
                <p>{t.totalPayment}: {emiResult.totalPayment}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
